const SocialLink = ({ name, href, Icon, className = "", hide }) => {
  return (
    <a aria-label={name} href={href} target={"_blank"} rel="noopener noreferrer" className={hide ? "hidden md:inline-block" : ""}>
      <div className="rounded border bg-[#e4e2e6] p-1">
        <Icon className={cn("size-6 stroke-0", className)} color="#e4e2e6" fill="#262835" />
      </div>
    </a>
  )
}

const SocialLinks = ({ links }) => {
  return (
    <>
      <div className="mt-4 flex w-full max-w-full items-center gap-4">
        {links.map((link, index) => (
          <SocialLink key={index} {...link} hide={index >= 5} />
        ))}
      </div>
      <div className="mt-4 flex w-full max-w-full items-center gap-4 md:hidden">
        {links.map((link, index) => (index >= 5 ? <SocialLink key={index} {...link} hide={false} /> : null))}
      </div>
    </>
  )
}

